import React from 'react';
import './index.css';
import { useState } from 'react';
import ReactMapGL, { Marker } from 'react-map-gl';
import 'mapbox-gl/dist/mapbox-gl.css';
import SearchBar from '../SearchBar';



function Map() {
  const [viewport, setViewport] = useState({
    width: '100wh',
    height: '100vh',
    latitude: 48.866424, 
    longitude: 2.352390,
    zoom: 6,
  });

//   const [selectedLocation, setSelectedLocation] = useState(null);

  const MAPBOX_TOKEN = 'pk.eyJ1Ijoicm9tYW5qYWxhYmVydCIsImEiOiJja2hhbzUwNHgwa3Q1MnRwancxZ2JueWE0In0.HB5_TUzkvN3pg2MFP8V-vg';

  return (
    <div className="app-map">
    <ReactMapGL
    id='AppMap'
    mapStyle='mapbox://styles/mapbox/streets-v11'
    width="100%"
    height="100%"
    minZoom={2}
    maxPitch={0}
    dragRotate={false}
    mapboxApiAccessToken={MAPBOX_TOKEN}
      {...viewport}
      onViewportChange={nextViewport => setViewport(nextViewport)}
    />

    <Marker
    latitude='48.869394'
    longitude='2.355818'
    >
        <button class='marker-btn'>
            <img src="../../Assets/svg/placeholder.svg" alt="Location Icon"/>
        </button>
    </Marker>
    </div>
  );
}

export default Map;
