import React from 'react';
import './index.css';
import { useState } from 'react';
import ReactMapGL, { Marker } from 'react-map-gl';
import  { Popup } from 'react-map-gl';
import 'mapbox-gl/dist/mapbox-gl.css';
import SearchBar from '../SearchBar';



function Map() {
  const [viewport, setViewport] = useState({
    width: '100wh',
    height: '100vh',
    latitude: 48.866424, 
    longitude: 2.352390,
    zoom: 6,
  });

  const [selectedLocation, setSelectedLocation] = useState(null);

  const MAPBOX_TOKEN = 'pk.eyJ1Ijoicm9tYW5qYWxhYmVydCIsImEiOiJja2hhbzUwNHgwa3Q1MnRwancxZ2JueWE0In0.HB5_TUzkvN3pg2MFP8V-vg';

  const MOVIE1 = [48.869394, 2.355818];


  return (
    <div className="app-map">
    <ReactMapGL
    mapStyle='mapbox://styles/mapbox/streets-v11'
    width="100%"
    height="100%"
    minZoom={2}
    maxPitch={0}
    dragRotate={false}
    mapboxApiAccessToken={MAPBOX_TOKEN}
      {...viewport}
      onViewportChange={nextViewport => 
        setViewport(nextViewport)}
    >
    <Marker
    latitude= {MOVIE1[0]}
    longitude = {MOVIE1[1]}
    ><button className='marker-btn' onclick={(e) => {
        e.prevent.default();
        setSelectedLocation(MOVIE1);
    }}>
            <img src="../../Assets/svg/placeholder.svg" alt=""/>
        </button>
    </Marker>

    {selectedLocation ? (
        <Popup />
    ) : null }
    </ReactMapGL>
    </div>
  );
}

export default Map;
