import React from 'react';
import './index.css';



const SearchBar=(keyword, setKeyword) => {

  return (
  <div className='searchBar'>
     <form action="">
        <input value={keyword} type="text" placeholder="Search ..." onChange={(e) => setKeyword(e.target.value)}></input>
        <button type="submit"><i class="homeSearch">go</i></button>
      </form> 
      <div className='results'></div>
  </div>
  )
};


export default SearchBar;
