import React from 'react';
import './index.css';
import { useState } from 'react';
import ReactMapGL from 'react-map-gl';
import 'mapbox-gl/dist/mapbox-gl.css';


function Map() {
  const [viewport, setViewport] = useState({
    width: 800,
    height: 600,
    latitude: 48.866424, 
    longitude: 2.352390,
    zoom: 6
  });

  const MAPBOX_TOKEN = 'pk.eyJ1Ijoicm9tYW5qYWxhYmVydCIsImEiOiJja2hhbzUwNHgwa3Q1MnRwancxZ2JueWE0In0.HB5_TUzkvN3pg2MFP8V-vg';

  return (
    <div className="app-map">
    <ReactMapGL
    width="100%"
    height="100%"
    minZoom={2}
    maxPitch={0}
    dragRotate={false}
    mapboxApiAccessToken={MAPBOX_TOKEN}
      {...viewport}
      onViewportChange={nextViewport => setViewport(nextViewport)}
    />
    </div>
  );
}

export default Map;
